mapper.select:

SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (SELECT * FROM co ORDER BY co_id) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}


SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (SELECT * FROM CO ORDER BY co_id DESC) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}


SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (SELECT * FROM co WHERE co_address LIKE '%' || #{coText} || '%' ORDER BY co_id) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}
