mapper.update:

  	UPDATE "MEMBER" SET  
	M_PW = STANDARD_HASH(#{boardMemberPasswd}, 'SHA512')
	WHERE M_ID LIKE #{boardMemberId}

mapper.select:

SELECT *
FROM (
  SELECT A.*, ROWNUM AS RNUM
  FROM (
    SELECT *
    FROM MEMBER_PET_VIEW
    ORDER BY MEMBER_ID
  ) A
  WHERE ROWNUM <= #{endRow}
)
WHERE RNUM > #{startRow}

mapper.update:

UPDATE "MEMBER" SET  
  M_PW = STANDARD_HASH(#{boardMemberPasswd}, 'SHA512')
WHERE M_ID = #{boardMemberId}

mapper.select:

SELECT *
FROM (
  SELECT A.*, ROWNUM AS RNUM
  FROM (
    SELECT *
    FROM MEMBER_PET_VIEW
    WHERE PET_WALKPROFILE = 'T'
    ORDER BY MEMBER_ID
  ) A
  WHERE ROWNUM <= #{endRow}
)
WHERE RNUM > #{startRow}


SELECT *
FROM (
  SELECT A.*, ROWNUM AS RNUM
  FROM (
    SELECT *
    FROM MEMBER_PET_VIEW
    WHERE PET_WALKPROFILE = 'T' AND MEMBER_REGION LIKE '%' || #{addressText} || '%'
    ORDER BY MEMBER_ID
  ) A
  WHERE ROWNUM <= #{endRow}
)
WHERE RNUM > #{startRow}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END AS is_favorite
FROM w_mate_fav
WHERE m_id = #{loginId} AND mate = #{targetId}
