mapper.select:

SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (SELECT * FROM SHOW_BOARD WHERE SHOW_TITLE LIKE '%' || #{showText} || '%' ORDER BY SHOW_NO) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}


SELECT * FROM (
    SELECT ROWNUM AS rnum, a.*
    FROM (SELECT * FROM SHOW_BOARD ORDER BY TO_NUMBER(SHOW_NO)) a
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}
